package StructureClasses;


public abstract class Personne implements Identifiable {

    public static final char HOMME = 'H', FEMME = 'F';
    public static String DEFAULT_IMAGD_BASE64_STRING ="";
    private String id, nom, prenom, email;
    private char sexe;

    public Personne()   {

    }
    public Personne(String nom, String prenom, char sexe)  {

        this.nom = nom;
        this.prenom = prenom;
        this.sexe = sexe;
    }

    public String getId() {
        return id;
    }

    public void setId(String id) {
        this.id = id;
    }

    public String getNom() {
        return nom;
    }

    public void setNom(String nom) {
        this.nom = nom;
    }

    public String getPrenom() {
        return prenom;
    }

    public void setPrenom(String prenom) {
        this.prenom = prenom;
    }

    public String getEmail() {
        return email;
    }

    public void setEmail(String email) {
        this.email = email;
    }

    public char getSexe() {
        return sexe;
    }

    public void setSexe(char sexe) {
        this.sexe = sexe;
    }

    @Override
    public String toString() {
        return String.format("%s %s", this.nom, this.prenom);
    }
}
